findError <- function(distributions) {			for(i in 1:length(distributions))	{		if(i == 1)		{			M = c(mean(distributions[[i]]))			SD = c(sd(distributions[[i]]))			N = c(length(distributions[[i]]))		}				else		{			M = c(M, mean(distributions[[i]]))			SD = c(SD, sd(distributions[[i]]))			N = c(N, length(distributions[[i]]))								}	}		SE = 0	df = 5000		for(i in 1:length(distributions))	{		SE = SE + (SD[i]*SD[i])/N[i]		if(df > N[i])			df = N[i]	}		df = df - 1;	SE = sqrt(SE)		error = qt(0.975, df)*SE	list(error = error);}