performMixedDesignANOVA <- function(dependentVariable, withinGroupVariable, betweenGroupVariable, participantVariable, dataset){         table <- as.data.frame(dataset);        IV = c(betweenGroupVariable, withinGroupVariable)    levelsA = eval(parse(text = paste("unique(table$", IV[1], ")", sep="")));    levelsB = eval(parse(text = paste("unique(table$", IV[2], ")", sep="")));    for(i in 1:length(levelsA))    {        for(j in 1:length(levelsB))        {            if(j == 1 && i == 1)            {                distributions = c(eval(parse(text = paste("list(subset(subset(table, ", betweenGroupVariable, " == \"", levelsA[i], "\"), ", withinGroupVariable, " == \"", levelsB[j], "\")$", dependentVariable, ")", sep=""))))            }            else            {                distributions = c(distributions, eval(parse(text = paste("list(subset(subset(table, ", betweenGroupVariable, " == \"", levelsA[i], "\"), ", withinGroupVariable, " == \"", levelsB[j], "\")$", dependentVariable, ")", sep=""))))            }        }    }        result = findError(distributions);    error = result$error;        result <- eval(parse(text = paste("ez::ezANOVA(table,dv=",dependentVariable,",wid=",participantVariable,",within=",withinGroupVariable,",between=",betweenGroupVariable,")",sep="")));    result <- result$ANOVA;        list(numDF = result$DFn, denomDF = result$DFd, F = result$F, p = result$p, etaSquared = result$ges, error = error);    }