performPairwiseTTest <- function(dependentVariable, independentVariable, dataset, varianceEqual = "TRUE", paired = "FALSE"){    dataset <- as.data.frame(dataset);        dependentVariable <- c(dependentVariable);    independentVariable <- c(independentVariable);        result = pairwise.t.test(dependentVariable, independentVariable, p.adjust="bonferroni", var.equal=T, paired=F);        list(p = result[["p.value"]]);}