performPairwiseWilcoxTest <- function(dependentVariable, independentVariable, dataset, varianceEqual = "TRUE", paired = "FALSE", independentVariableName, levelA, levelB){    dataset <- as.data.frame(dataset);        dependentVariable <- c(dependentVariable);    independentVariable <- c(independentVariable);        result = pairwise.wilcox.test(dependentVariable, independentVariable, p.adjust="bonferroni", var.equal=T, paired=F);        levels = eval(parse(text = paste("sort(unique(dataset$",independentVariableName,"))",sep="")));        p = result[["p.value"]];        val = 0;        if(levelA == levels[length(levels)])    {        val = p[levelA, levelB];    }    else    {        if(levelA < levelB)        {            val = p[levelB, levelA];        }        else        {            val = p[levelA, levelB];        }    }            list(p = val);}