performShapiroWilkTestForMultipleDistributions <- function(distributions){    dist <- as.data.frame(distributions);    for(i in 1:length(dist))  {     result <- eval(parse(text = paste("shapiro.test(dist)")));  }    list(testStatistic = result$statistic[["W"]], p = result$p.value, method = result$method, len = length(dist));}