writeToFile <- function(object, fileName){	tempSubpath <- "VisiStatLog"	tempFullPath <- file.path(tempdir(), tempSubpath)	tempFullPath <- gsub("//", "/", tempFullPath)  # fix R bug that generate double slashes in OS X	dir.create(tempFullPath, showWarnings = FALSE)    write.csv(object, paste(tempFullPath, fileName, ".txt", sep=""), quote=F, row.names=T);}